import React from 'react';
import './Header.css';
import LupaIcon from '../assets/icons/Lupa de Busqueda Superior.svg'; // Ruta del ícono de la lupa

const Header = ({ userName, userLocation }) => {
  return (
    <div className="header">
      <div className="search-container">
        <img src={LupaIcon} alt="Buscar" className="search-icon" />
        <input
          type="text"
          placeholder="Buscar..."
          className="search-bar"
        />
      </div>
      <div className="user-info">
        <span className="user-name">{userName}</span>
        <span className="user-location">{userLocation}</span>
      </div>
    </div>
  );
};

export default Header;
