import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Home from './pages/Home';
import OpcionesActivosPage from './components/OpcionesActivos'; // Importar la nueva página

function App() {
  const userData = {
    name: 'Francisco Hernandez',
    location: 'UNP, Managua',
  };

  return (
    <Router>
      <div className="app">
        <Sidebar />
        <div className="main-section">
          <Header userName={userData.name} userLocation={userData.location} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/opciones-activos" element={<OpcionesActivosPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
